div.onclick:
var bOptionID = this.parentNode.getAttribute('bOptionID');
var subID = this.getAttribute('subID');
document.getElementById(bOptionID).style.display='none' ; 
document.getElementById(bOptionID+'_TABL1').style.backgroundImage='url(uiplugin/chart/images/tab/tab_off_01.gif)' ; 
document.getElementById(bOptionID+'_TABL2').style.backgroundImage='url(uiplugin/chart/images/tab/tab_off_02.gif)' ; 
document.getElementById(bOptionID+'_TABL3').style.backgroundImage='url(uiplugin/chart/images/tab/tab_off_03.gif)' ; 
this.parentNode.setAttribute('bOptionID', subID);
